# [409] Longest Palindrome
#

# @lc code=start
from collections import Counter


class Solution:
    def longestPalindrome(self, s: str) -> int:
        ans = sum(f if f % 2 == 0 else f - 1 for f in Counter(s).values())
        if ans < len(s): ans += 1
        return ans
        
# @lc code=end

if __name__ == "__main__":
    s = ""
    print(Solution().longestPalindrome(s))


